import {
  Dialog,
  IconButton,
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import "./viewer.css";

const ProjectViewer = ({ open, onClose, project }) => {
  if (!project) return null;

  return (
    <Dialog
      open={open}
      onClose={onClose}
      fullWidth
      maxWidth="lg"
      PaperProps={{
        style: {
          backgroundColor: "#000",
          color: "#fff",
        },
      }}
    >
      <div className="viewer-container">
        {/* Close Button */}
        <IconButton className="viewer-close" onClick={onClose}>
          <CloseIcon />
        </IconButton>

        {/* Gallery */}
        <div className="viewer-gallery">
          {project.media.map((item, index) =>
            item.type === "image" ? (
              <img
                key={index}
                src={item.src}
                alt=""
                className="viewer-media"
              />
            ) : (
              <video
                key={index}
                src={item.src}
                controls
                className="viewer-media"
              />
            )
          )}
        </div>

        {/* Content */}
        <div className="viewer-content">
          <h2>{project.title}</h2>
          <p>{project.description2}</p>

          <button className="viewer-btn">
            View Live Project
          </button>
        </div>
      </div>
    </Dialog>
  );
};

export default ProjectViewer;
