import ProjectCard from "./card"
import {Grid} from "@mui/material"
import aura from './images/aura.png'
import erp from './images/erp.png'
import food from './images/food.png'
import ndc from './images/ndc.png'
import bd from './images/bd.png'
import ProjectViewer from "./view"
import { useState } from "react"
import { projects } from "./projects"

const Projects = () => {
    const [selectedProject, setSelectedProject] = useState(null)
  return (
    <section className="project-section">
        <h2 className="project-heading">Featured Projects</h2>
            <Grid container>
            {projects.map((project, idx) => (
              <Grid item xs={12} md={6} paddingTop={"2%"} key={idx}>
                <ProjectCard
                  image={project.media?.[0]?.src || project.image || erp}
                  title={project.title}
                  description={project.description}
                  tech={project.tech || []}
                  onClick={() => setSelectedProject(project)}
                />
              </Grid>
            ))}
        </Grid>
        <ProjectViewer
            open={Boolean(selectedProject)}
            project={selectedProject}
            onClose={() => setSelectedProject(null)}
        />

    </section>
  );
};

export default Projects;
